// import noCard from "../../assets/no-card.svg";
import { useContext } from "react";
import { TransactionsContext } from "../../../contexts/TransactionsContext";
import { Card } from "./Card";

export const List = () => {
  const { listTransactions, filteredTransactions } =
    useContext(TransactionsContext);
  const listRender = filteredTransactions.length
    ? filteredTransactions
    : listTransactions;
  return (
    <ul className="list__container">
      {listRender.length
        ? listRender.map((transaction: any) => {
            return <Card key={transaction.id} transaction={transaction} />;
          })
        : //<img src={noCard} alt="Lista de transações vazia" />
          null}
    </ul>
  );
};
