import { useContext } from "react";
import {
  iTransaction,
  TransactionsContext,
} from "../../../../contexts/TransactionsContext";
import "./style.css";
import { StyledCard, StyledDescription, StyledValue } from "./styles";

interface iCardProps {
  transaction: iTransaction;
}

export const Card = ({ transaction }: iCardProps) => {
  const { delTransaction } = useContext(TransactionsContext);
  const { description, type, value } = transaction;

  return (
    <StyledCard type={type}>
      <StyledDescription>
        <h2>{description}</h2>
        <span>{type}</span>
      </StyledDescription>
      <StyledValue>
        <span>{value}</span>
        <button
          onClick={() => delTransaction(transaction)}
          className="button-default"
        ></button>
      </StyledValue>
    </StyledCard>
  );
};
