import "./style.css";
export const Card = ({ description, value, type, id, rmvTransaction }) => {
  return (
    <li className={type == "Saída" ? "card-output" : "card"}>
      <div className="description-type">
        <h2>{description}</h2>
        <span>{type}</span>
      </div>
      <div className="value-btn">
        <span>{value}</span>
        <button
          className="button-default"
          onClick={(event) => rmvTransaction(event.target.id)}
          id={id}
        ></button>
      </div>
    </li>
  );
};
