import styled from "styled-components";

export const StyledTransactions = styled.section`
  width: 100%;
  max-width: 600px;
`;

export const StyledOptions = styled.div`
  margin-bottom: 24px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  gap: 20px;

  h3 {
    font-size: var(--sz-text-1);
    font-weight: 600;
  }
`;
