import { createContext, useState } from "react";
import {
  iTransaction,
  iTransactionContext,
  iTransactionsProviderProps,
} from "./types";
import { Global } from "../../utils/global";

export const TransactionsContext = createContext({} as iTransactionContext);

export const TransactionsProvider = ({
  children,
}: iTransactionsProviderProps) => {
  const [Transactions, setTransactions] = useState<iTransaction[] | []>([]);
  const [filteredTransactions, setFilteredTransaction] = useState(
    [] as iTransaction[]
  );

  const handleAddTransaction = ({
    description,
    type,
    value,
  }: iTransaction): void => {
    if (description.trim() != "") {
      const transaction = {
        description: description,
        type: type,
        value: value,
      };
      const newListTransactions = Global.handleGeneratorIds([
        ...listTransactions,
        transaction,
      ]);
      setListTransactions(newListTransactions);
    }
  };

  const handleRmvTransaction = (selectedTransaction: iTransaction): void => {
    const newListTransactions = listTransactions.filter(
      ({ id }) => id != selectedTransaction.id
    );
    setListTransactions(newListTransactions);
  };

  const handleFilterTransactions = (selectedType: string): void => {
    if (selectedType == "all") {
      setFilteredTransaction([]);
    } else {
      const listFiltered = listTransactions.filter(
        ({ type }) => type == selectedType
      );
      setFilteredTransaction(listFiltered);
    }
  };

  return (
    <TransactionsContext.Provider
      value={{
        listTransactions,
        handleAddTransaction,
        handleRmvTransaction,
        handleFilterTransactions,
        filteredTransactions,
      }}
    >
      {children}
    </TransactionsContext.Provider>
  );
};
