import { useState } from "react";
import "./style.css";
export const Form = ({ listTransactions, setListTransactions }) => {
  const [description, setDescription] = useState("");
  const [value, setValue] = useState("");
  const [type, setType] = useState("");

  const addTransactions = (event, callback) => {
    event.preventDefault();
    const transaction = {
      description,
      value,
      type,
    };
    callback(transaction);
  };
  return (
    <form
      className="form__container"
      onSubmit={(event) => addTransactions(event)}
    >
      <div className="description__container">
        <label htmlFor="description">Descrição</label>
        <input
          type="text"
          name="description"
          id="description"
          placeholder="Digite aqui sua descrição"
        />
        <span className="description__msg">Ex: Compra de roupas</span>
      </div>
      <div className="value-type__container">
        <div className="value__container">
          <span>R$</span>
          <label htmlFor="value">Valor</label>
          <input type="text" name="value" id="value" />
        </div>
        <div className="type__container">
          <label htmlFor="type">Tipo de valor</label>
          <select name="type" id="type">
            <option value="">Entrada</option>
            <option value="">Saída</option>
          </select>
        </div>
      </div>

      <button>Inserir valor</button>
    </form>
  );
};
