export class Global {
  static handleValueFormat(arrList) {
    return arrList.map((transaction) => {
      let { value } = transaction;
      value = value.replaceAll(",", "").replaceAll(".", "");

      const valueFormat = Number(value).toLocaleString("pt-BR", {
        currency: "BRL",
        style: "currency",
        minimumIntegerDigits: 2,
      });

      return {
        ...transaction,
        value: valueFormat,
      };
    });
  }
}
