import "./style.css";
export const Card = ({ description, value, type, id, rmvTransaction }) => {
  value = Number(value).toLocaleString("pt-BR", {
    currency: "BRL",
    style: "currency",
    minimumIntegerDigits: 2,
  });

  return (
    <li className="card">
      <div className="description-type">
        <h2>{description}</h2>
        <span>{type}</span>
      </div>
      <div className="value-btn">
        <span>{value}</span>
        <button
          className="button-default"
          onClick={(event) => rmvTransaction(event.target.id)}
          id={id}
        >
          <i class="fa-solid fa-trash"></i>
        </button>
      </div>
    </li>
  );
};
