import "./style.css";
import { Card } from "../Card";
import noCard from "../../assets/no-card.svg";
import { Global } from "../../Models/Global/Global";
import { useState } from "react";

let listRender = [];
export const List = ({
  listTransactions,
  setListTransactions,
  setIsfilter,
  // listForRender,
  // rmvTransaction,
}) => {
  const [allBtnActive, setAllBtnActive] = useState(true);
  const [inputBtnActive, setInputBtnActive] = useState(false);
  const [outputBtnActive, setOutputBtnActive] = useState(false);
  const [listTransactionsFilter, setListTransactionsFilter] = useState([]);

  // if (isFilter) {
  //   listRender = [...listTransactionsFilter];
  // } else {
  //   listRender = [...listTransactions];
  // }

  // listForRender = Global.handleValueFormat(listForRender);

  const transactionsFiltered = (idBtn) => {
    if (idBtn != "all") {
      if (idBtn == "input") {
        idBtn = "Entrada";
      } else if (idBtn == "output") {
        idBtn = "Saída";
      }
      const listFiltered = listTransactions.filter(({ type }) => type == idBtn);
      setListTransactionsFilter(listFiltered);
    } else {
      setListTransactions((arrList) => {
        return arrList;
      });
    }
  };

  return (
    <section className="section-list__container">
      <div className="header-list__container">
        <h3>Resumo financeiro</h3>
        <div className="btns__container">
          <button
            onClick={(event) => {
              setAllBtnActive(true);
              setInputBtnActive(false);
              setOutputBtnActive(false);
              transactionsFiltered(event.target.id);
              setIsfilter(false);
            }}
            className={
              allBtnActive ? "button-default button-active" : "button-default"
            }
            id="all"
          >
            Todos
          </button>
          <button
            onClick={(event) => {
              setInputBtnActive(true);
              setAllBtnActive(false);
              setOutputBtnActive(false);
              transactionsFiltered(event.target.id);
              setIsfilter(true);
            }}
            className={
              inputBtnActive ? "button-default button-active" : "button-default"
            }
            id="input"
          >
            Entradas
          </button>
          <button
            onClick={(event) => {
              setOutputBtnActive(true);
              setInputBtnActive(false);
              setAllBtnActive(false);
              transactionsFiltered(event.target.id);
              setIsfilter(true);
            }}
            className={
              outputBtnActive
                ? "button-default button-active"
                : "button-default"
            }
            id="output"
          >
            Despesas
          </button>
        </div>
      </div>
      <ul className="list__container">
        {listRender.length ? (
          listRender.map((transaction) => {
            const { description, value, type, id } = transaction;

            return (
              <Card
                description={description}
                value={value}
                type={type}
                id={id}
                // rmvTransaction={rmvTransaction}
              />
            );
          })
        ) : (
          <img src={noCard} alt="" />
        )}
      </ul>
    </section>
  );
};
