// import noCard from "../../assets/no-card.svg";
import { useContext } from "react";
import { TransactionsContext } from "../../../contexts/TransactionsContext";
import { Card } from "./Card";
import "./style.css";

export const List = () => {
  const { listTransactions } = useContext(TransactionsContext);

  return (
    <ul className="list__container">
      {listTransactions.length
        ? listTransactions.map((transaction: any) => {
            const { description, value, type, id } = transaction;

            return (
              <Card
                key={id}
                description={description}
                value={value}
                type={type}
                id={id}
              />
            );
          })
        : // <img src={noCard} alt="Lista de transações vazia" />
          null}
    </ul>
  );
};
