import React, { createContext, useState } from "react";

interface iCreateTransaction {
  description: string;
  type: string;
  value: string;
}

interface iTransactionsProviderProps {
  children: React.ReactNode;
}

interface iTransactionProvider {
  handleCreateTransaction: ({
    description,
    type,
    value,
  }: iCreateTransaction) => void;
}

export const TransactionsContext = createContext({} as iTransactionProvider);

export const TransactionsProvider = ({
  children,
}: iTransactionsProviderProps) => {
  // const [ transactionsList, setTransactionsList ] = useState()
  const handleCreateTransaction = ({
    description,
    type,
    value,
  }: iCreateTransaction) => {
    const valueFormated = Number(value).toLocaleString("pt-BR", {
      currency: "BRL",
      style: "currency",
      minimumIntegerDigits: 2,
    });

    console.log(valueFormated);

    const transaction = {
      description: description,
      type: type,
      value: valueFormated,
    };
  };

  // const addTransactions = (trasaction) => {};

  return (
    <TransactionsContext.Provider value={{ handleCreateTransaction }}>
      {children}
    </TransactionsContext.Provider>
  );
};
