import "./style.css";
import { Card } from "../Card";
import noCard from "../../assets/no-card.svg";

export const List = ({ listTransactions, setListTransactions }) => {
  const rmvTransaction = (idTransaction) => {
    const arrList = listTransactions.filter(({ id }) => id != idTransaction);
    setListTransactions(arrList);
  };

  return (
    <section className="section-list__container">
      <div className="header-list__container">
        <h3>Resumo financeiro</h3>
        <div className="btns__container">
          <button>Todos</button>
          <button>Entradas</button>
          <button>Despesas</button>
        </div>
      </div>
      <ul className="list__container">
        {/* <img src={noCard} alt="" /> */}

        {listTransactions.length > 0 ? (
          listTransactions.map((transaction) => {
            const { description, value, type } = transaction;

            return (
              <Card
                description={description}
                value={value}
                type={type}
                rmvTransaction={rmvTransaction}
              />
            );
          })
        ) : (
          <img src={noCard} alt="" />
        )}

        {/* <Card /> */}
      </ul>
    </section>
  );
};
