import "./style.css";
export const TotalMoney = ({ listTransactions }) => {
  const total = listTransactions.reduce((acc, cur) => {
    return acc + +cur.value;
  }, 0);

  return (
    <section className="money__container">
      <div className="money-total__container">
        <p>Valor total:</p>
        <p>R$: {total}</p>
      </div>
      <span>O valor se refere ao saldo</span>
    </section>
  );
};
