import { useState } from "react";
import { Form } from "./components/Form";
import { Header } from "./components/Header";
import { List } from "./components/List";
import { IdsGenerator } from "./Models/IdsGenerator/IdsGenerator.js";

let listTransactionsOrig = [];

const App = () => {
  const [listTransactions, setListTransactions] = useState([]);

  const addTransactions = (transaction) => {
    let arrTransactions = IdsGenerator.handleIds(listTransactionsOrig);

    const valueTransactionFormat = () => {
      return arrTransactions.map((transaction) => {
        const { value } = transaction.value
          .replaceAll(",", "")
          .replaceAll(".", "");
        console.log(value);
        const valueFormat = Number(value).toLocaleString("pt-BR", {
          currency: "BRL",
          style: "currency",
          minimumIntegerDigits: 2,
        });

        return {
          ...transaction,
          value: valueFormat,
        };
      });
    };
    arrTransactions = valueTransactionFormat();

    console.log(arrTransactions);
    listTransactionsOrig = [...arrTransactions, transaction];
    setListTransactions(listTransactionsOrig);
  };

  const rmvTransaction = (idTransaction) => {
    const arrTransactionsFilterOri = listTransactionsOrig.filter(
      ({ id }) => id != idTransaction
    );

    const arrTransactionsFilter = listTransactions.filter(
      ({ id }) => id != idTransaction
    );
    listTransactionsOrig = [...arrTransactionsFilterOri];

    setListTransactions(arrTransactionsFilter);
  };

  const filterTransactions = (tp) => {
    if (tp == "all") {
      setListTransactions(listTransactionsOrig);
    } else {
      const arrFilter = listTransactionsOrig.filter(({ type }) => tp == type);

      setListTransactions(arrFilter);
    }
  };

  return (
    <>
      <Header />
      <main className="container main__container">
        <Form
          addTransactions={addTransactions}
          listTransactions={listTransactions}
        />
        <List
          listTransactions={listTransactions}
          rmvTransaction={rmvTransaction}
          filterTransactions={filterTransactions}
        />
      </main>
    </>
  );
};

export default App;
