import { useState } from "react";
import { Form } from "./components/Form";
import { Header } from "./components/Header";
import { LandPage } from "./components/LandPage";
import { List } from "./components/List";
import { Global } from "./Models/Global/Global";

// let listTransactionsOrig = [];

let listForRender = [];
const App = () => {
  const [listTransactions, setListTransactions] = useState([]);
  const [listTransactionsFilter, setListTransactionsFilter] = useState([]);
  const [isLogged, setIsLogged] = useState(false);

  const checkLogged = () => {
    setIsLogged(!isLogged);
  };

  const addTransactions = (transaction) => {
    let arrTransactions = [...listTransactions, transaction];
    arrTransactions = Global.handleIds(arrTransactions);
    listForRender = [arrTransactions];
    setListTransactions(arrTransactions);
  };

  const rmvTransaction = (idTransaction) => {
    const newArrTransactions = listTransactions.filter(
      ({ id }) => id != idTransaction
    );

    const arrTransactionsFilter = listTransactionsFilter.filter(
      ({ id }) => id != idTransaction
    );

    if (arrTransactionsFilter.length > 0) {
      listForRender = [...arrTransactionsFilter];
    } else {
      listForRender = [...newArrTransactions];
    }
    setListTransactions(newArrTransactions);
    setListTransactionsFilter(arrTransactionsFilter);
  };

  const filterTransactions = (tp) => {
    if (tp == "all") {
      setListTransactionsFilter([]);
      listForRender = [...listTransactions];
      setListTransactions(listTransactions);
    } else {
      const arrFilter = listTransactions.filter(({ type }) => tp == type);
      listForRender = [...arrFilter];
      setListTransactionsFilter(arrFilter);
    }
  };

  return (
    <>
      {isLogged ? (
        <>
          <Header checkLogged={checkLogged} />
          <main className="container main__container">
            <Form
              addTransactions={addTransactions}
              listTransactions={listTransactions}
            />
            <List
              listTransactions={listTransactions}
              listTransactionsFilter={listTransactionsFilter}
              listForRender={listForRender}
              rmvTransaction={rmvTransaction}
              filterTransactions={filterTransactions}
            />
          </main>
        </>
      ) : (
        <LandPage checkLogged={checkLogged} />
      )}
    </>
  );
};

export default App;
