import "./style.css";
export const TotalMoney = ({ arrList }) => {
  const total = Number(
    arrList.reduce((acc, cur) => {
      if (cur.type == "Saída") {
        return acc - +cur.value;
      }

      return acc + +cur.value;
    }, 0)
  ).toLocaleString("pt-BR", {
    currency: "BRL",
    style: "currency",
    minimumIntegerDigits: 2,
  });

  return (
    <section className="money__container">
      <div className="money-total__container">
        <p>Valor total:</p>
        <p>{total}</p>
      </div>
      <span>O valor se refere ao saldo</span>
    </section>
  );
};
