import React, { createContext, useState } from "react";
import { Global } from "../utils/global";

export interface iTransaction {
  id?: number;
  description: string;
  type: string;
  value: string;
}

interface iTransactionsProviderProps {
  children: React.ReactNode;
}

interface iTransactionProvider {
  handleCreateTransaction: ({ description, type, value }: iTransaction) => void;
  transactionsList: iTransaction[];
  handleDeletTransaction: (selectedTransaction: iTransaction) => void;
}

export const TransactionsContext = createContext({} as iTransactionProvider);

export const TransactionsProvider = ({
  children,
}: iTransactionsProviderProps) => {
  const [transactionsList, setTransactionsList] = useState<[] | iTransaction[]>(
    []
  );

  const handleCreateTransaction = ({
    description,
    type,
    value,
  }: iTransaction): void => {
    const transaction = {
      description: description,
      type: type,
      value: value,
    };

    const newTransactionsList = Global.handleGeneratorIds([
      ...transactionsList,
      transaction,
    ]);

    setTransactionsList(newTransactionsList);
  };

  const handleDeletTransaction = (selectedTransaction) => {
    const transactionsFiltered = transactionsList.filter(
      ({ id }) => id != selectedTransaction.id
    );
    setTransactionsList(transactionsFiltered);
  };

  return (
    <TransactionsContext.Provider
      value={{
        transactionsList,
        handleCreateTransaction,
        handleDeletTransaction,
      }}
    >
      {children}
    </TransactionsContext.Provider>
  );
};
