import { useState } from "react";
import { Form } from "./components/Form";
import { Header } from "./components/Header";
import { List } from "./components/List";
import { IdsGenerator } from "./Models/IdsGenerator/IdsGenerator.js";

let listTransactionsOrig = [];

const App = () => {
  const [listTransactions, setListTransactions] = useState([]);
  console.log(listTransactionsOrig);

  const addTransactions = (transaction) => {
    const arrTransactions = IdsGenerator.handleIds(listTransactions);
    console.log(arrTransactions);
    listTransactionsOrig = [...arrTransactions];
    setListTransactions([...arrTransactions, transaction]);
  };

  const rmvTransaction = (idTransaction) => {
    const arrTransactionsFilter = listTransactions.filter(
      ({ id }) => id != idTransaction
    );
    listTransactionsOrig = [...arrTransactionsFilter];
    setListTransactions(arrTransactionsFilter);
  };

  const filterTransactions = (tp) => {
    const arrFilter = listTransactionsOrig.filter(({ type }) => tp == type);
    console.log(arrFilter);
    setListTransactions(arrFilter);
  };

  console.log(listTransactionsOrig);
  console.log(listTransactions);

  return (
    <>
      <Header />
      <main className="container main__container">
        <Form addTransactions={addTransactions} />
        <List
          listTransactions={listTransactions}
          rmvTransaction={rmvTransaction}
          filterTransactions={filterTransactions}
        />
      </main>
    </>
  );
};

export default App;
