import { useState } from "react";

export const Transactions = () => {
  const [inputBtnActive, setInputBtnActive] = useState(false);
  const [outputBtnActive, setOutputBtnActive] = useState(false);
  const [isFilter, setIsFilter] = useState(false);

  // arrList = Global.handleValueFormat(arrList);

  return (
    <section className="section-list__container">
      <div className="header-list__container">
        <h3>Resumo financeiro</h3>
        <div className="btns__container">
          <button
            onClick={(event) => {
              setInputBtnActive(false);
              setOutputBtnActive(false);
              transactionsFiltered(event.target.id);
            }}
            className={!isFilter && "button-active"}
            id="all"
          >
            Todos
          </button>
          <button
            onClick={(event) => {
              setInputBtnActive(true);
              setOutputBtnActive(false);
              transactionsFiltered(event.target.id);
            }}
            className={inputBtnActive && isFilter && "button-active"}
            id="input"
          >
            Entradas
          </button>
          <button
            onClick={(event) => {
              setOutputBtnActive(true);
              setInputBtnActive(false);
              transactionsFiltered(event.target.id);
            }}
            className={outputBtnActive && isFilter && "button-active"}
            id="output"
          >
            Despesas
          </button>
        </div>
      </div>
    </section>
  );
};
