import { iTransaction } from "../contexts/TransactionsContext";

export class Global {
  static handleValueFormat(listTransactions: iTransaction[]) {
    return listTransactions.map((transaction: iTransaction) => {
      const value = transaction.value;
      const valueFormatted = Number(value).toLocaleString("pt-BR", {
        currency: "BRL",
        style: "currency",
        minimumIntegerDigits: 2,
      });

      return {
        ...transaction,
        value: valueFormatted,
      };
    });
  }

  static handleGeneratorIds(list: iTransaction) {
    return list.map((item: iTransaction, index: number) => {
      return {
        ...item,
        id: index,
      };
    });
  }
}
