import React, { createContext, useState } from "react";
import { Global } from "../utils/global";

export interface iTransaction {
  id?: number;
  description: string;
  type: string;
  value: string;
}

interface iTransactionsProviderProps {
  children: React.ReactNode;
}

interface iTransactionProvider {
  createTransaction: ({ description, type, value }: iTransaction) => void;
  listTransactions: iTransaction[];
  delTransaction: (selectedTransaction: iTransaction) => void;
}

export const TransactionsContext = createContext({} as iTransactionProvider);

export const TransactionsProvider = ({
  children,
}: iTransactionsProviderProps) => {
  const [listTransactions, setListTransactions] = useState<[] | iTransaction[]>(
    []
  );

  const createTransaction = ({
    description,
    type,
    value,
  }: iTransaction): void => {
    const transaction = { description: description, type: type, value: value };

    const newListTransactions = Global.handleGeneratorIds([
      ...listTransactions,
      transaction,
    ]);

    setListTransactions(newListTransactions);
  };

  const delTransaction = (selectedTransaction: iTransaction) => {
    const newListTransactions = listTransactions.filter(
      ({ id }) => id != selectedTransaction.id
    );
    setListTransactions(newListTransactions);
  };

  return (
    <TransactionsContext.Provider
      value={{
        listTransactions,
        createTransaction,
        delTransaction,
      }}
    >
      {children}
    </TransactionsContext.Provider>
  );
};
