import { FormEvent, useContext, useState } from "react";
import { TransactionsContext } from "../../contexts/TransactionsContext";
import "./style.css";
import { TotalMoney } from "./TotalMoney";

export const Form = () => {
  const { transactionsList, handleCreateTransaction } =
    useContext(TransactionsContext);

  const [inputDescription, setInputDescription] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [inputType, setInputType] = useState("Entrada");

  const handleSubmit = (e: FormEvent): void => {
    e.preventDefault();

    handleCreateTransaction({
      description: inputDescription,
      type: inputType,
      value: inputValue,
    });
  };

  return (
    <section>
      <form className="form__container" onSubmit={handleSubmit}>
        <div className="description__container">
          <label htmlFor="description">Descrição</label>
          <input
            onChange={(event) => setInputDescription(event.target.value)}
            value={inputDescription}
            type="text"
            name="description"
            id="description"
            placeholder="Digite aqui sua descrição"
          />
          <span className="description__msg">Ex: Compra de roupas</span>
        </div>
        <div className="value-type__container">
          <div className="value__container">
            <span>R$</span>
            <label htmlFor="value">Valor</label>
            <input
              onChange={(event) => setInputValue(event.target.value)}
              type="number"
              name="value"
              id="value"
              required
            />
          </div>
          <div className="type__container">
            <label htmlFor="type">Tipo de valor</label>
            <select
              onChange={(event) => setInputType(event.target.value)}
              name="type"
              id="type"
              value={inputType}
            >
              <option value="Entrada">Entrada</option>
              <option value="Saída">Saída</option>
            </select>
          </div>
        </div>

        <button className="button-active">Inserir valor</button>
      </form>

      <TotalMoney />
    </section>
  );
};
