import "./style.css";
import { Card } from "../Card";
import noCard from "../../assets/no-card.svg";
import { useState } from "react";

export const List = ({ listTransactions, rmvTransaction }) => {
  const [listFilter, setListFilter] = useState([]);

  let listRender = [];

  if (listFilter.length) {
    listRender = listFilter;
  } else {
    listRender = listTransactions;
  }

  const filter = (idBtn) => {
    if (idBtn == "input") {
      idBtn = "Entrada";
    } else {
      idBtn = "Saída";
    }

    const arrFilter = listTransactions.filter(({ type }) => type == idBtn);
    setListFilter(arrFilter);
  };

  console.log(listRender);

  return (
    <section className="section-list__container">
      <div className="header-list__container">
        <h3>Resumo financeiro</h3>
        <div className="btns__container">
          <button className="button-default button-active">Todos</button>
          <button
            onClick={(event) => filter(event.target.id)}
            className="button-default"
            id="input"
          >
            Entradas
          </button>
          <button
            onClick={(event) => filter(event.target.id)}
            className="button-default"
            id="output"
          >
            Despesas
          </button>
        </div>
      </div>
      <ul className="list__container">
        {listRender.length > 0 ? (
          listRender.map((transaction) => {
            const { description, value, type, id } = transaction;

            return (
              <Card
                description={description}
                value={value}
                type={type}
                id={id}
                rmvTransaction={rmvTransaction}
              />
            );
          })
        ) : (
          <img src={noCard} alt="" />
        )}
      </ul>
    </section>
  );
};
