import "./style.css";
import { Card } from "../Card";
import noCard from "../../assets/no-card.svg";
import { Global } from "../../Models/Global/Global";
import { useState } from "react";

export const List = ({ listForRender, rmvTransaction, filterTransactions }) => {
  const [btnActive, setBtnActive] = useState(false);

  const filter = (idBtn) => {
    if (idBtn == "input") {
      idBtn = "Entrada";
    } else {
      idBtn = "Saída";
    }
    filterTransactions(idBtn);
  };

  listForRender = Global.handleValueFormat(listForRender);

  return (
    <section className="section-list__container">
      <div className="header-list__container">
        <h3>Resumo financeiro</h3>
        <div className="btns__container">
          <button
            onClick={() => filterTransactions("all")}
            className="button-default button-active"
          >
            Todos
          </button>
          <button
            onClick={(event) => {
              setBtnActive(!btnActive);
              filter(event.target.id);
            }}
            className={
              btnActive ? "button-default button-active" : "button-default"
            }
            // className="button-default"
            id="input"
          >
            Entradas
          </button>
          <button
            onClick={(event) => filter(event.target.id)}
            className="button-default"
            id="output"
          >
            Despesas
          </button>
        </div>
      </div>
      <ul className="list__container">
        {listForRender.length ? (
          listForRender.map((transaction) => {
            const { description, value, type, id } = transaction;

            return (
              <Card
                description={description}
                value={value}
                type={type}
                id={id}
                rmvTransaction={rmvTransaction}
              />
            );
          })
        ) : (
          <img src={noCard} alt="" />
        )}
      </ul>
    </section>
  );
};
