import { Header } from "../../components/Header";
import { Form } from "../../components/Form";
import { List } from "../../components/List";
import { useState } from "react";
import { Global } from "../../Models/Global/Global";

let listForRender = [];
export const Dashboard = ({ checkLogged }) => {
  const [listTransactions, setListTransactions] = useState([]);
  const [listTransactionsFilter, setListTransactionsFilter] = useState([]);

  const addTransactions = (transaction) => {
    const arrTransactions = Global.handleIds([
      ...listTransactions,
      transaction,
    ]);
    console.log(arrTransactions);
    listForRender = [...listTransactions];
    console.log(listForRender);
    setListTransactions(arrTransactions);
    console.log(listForRender);
  };

  const transactionsFiltered = (idBtn) => {
    if (idBtn != "all") {
      if (idBtn == "input") {
        idBtn = "Entrada";
      } else if (idBtn == "output") {
        idBtn = "Saída";
      }
      const listFiltered = listTransactions.filter(({ type }) => type == idBtn);
      setListTransactionsFilter(listFiltered);
      listForRender = [...listTransactionsFilter];
    } else {
      setListTransactionsFilter([]);
      setListTransactions(listTransactions);
      listForRender = [...listTransactions];
    }
  };

  return (
    <>
      <Header checkLogged={checkLogged} />
      <main className="container main__container">
        <Form addTransactions={addTransactions} />
        <List
          // rmvTransaction={rmvTransaction}
          listForRender={listForRender}
          transactionsFiltered={transactionsFiltered}
        />
      </main>
    </>
  );
};

