import { useContext, useState } from "react";
import { TransactionsContext } from "../../contexts/TransactionsContext";
import { StyledButton } from "../../styles/components/buttons";
import { List } from "./List";
import { StyledOptions, StyledTransactions } from "./styles";

export const Transactions = () => {
  const { filterTransactions } = useContext(TransactionsContext);
  const [inputBtnActive, setInputBtnActive] = useState(false);
  const [outputBtnActive, setOutputBtnActive] = useState(false);
  const [isFilter, setIsFilter] = useState(false);

  return (
    <StyledTransactions>
      <StyledOptions className="header-list__container">
        <h3>Resumo financeiro</h3>

        <div className="btns__container">
          <StyledButton
            variant="secondary"
            onClick={() => {
              filterTransactions("all");
            }}
          >
            Todos
          </StyledButton>
          <StyledButton
            variant="secondary"
            onClick={() => {
              filterTransactions("Entrada");
            }}
          >
            Entradas
          </StyledButton>
          <StyledButton
            variant="secondary"
            onClick={() => {
              filterTransactions("Saída");
            }}
          >
            Despesas
          </StyledButton>
        </div>
      </StyledOptions>

      <List />
    </StyledTransactions>
  );
};
