export class Global {
  static handleValueFormat(listTransactions) {
    return listTransactions.map((transaction) => {
      let value = transaction.value;
      value = Number(value).toLocaleString("pt-BR", {
        currency: "BRL",
        style: "currency",
        minimumIntegerDigits: 2,
      });

      return {
        ...transaction,
        value,
      };
    });
  }

  static handleValueClean(listTransactions) {
    return listTransactions.map((transaction) => {
      console.log(transaction);
      let value = transaction.value;
      console.log(value);
      value = value.replaceAll(",", "").replaceAll(".", "");

      return {
        ...transaction,
        value,
      };
    });
  }
}
