import "./style.css";
export const Card = ({ description, value, type }) => {
  console.log(+value);

  const nValue = +value.toLocaleString("pt-BR", {
    currency: "BRL",
    style: "currency",
  });

  console.log(nvalue);
  return (
    <li className="card">
      <div className="description-type">
        <h2>{description}</h2>
        <span>{type}</span>
      </div>
      <div className="value-btn">
        <span>{value}</span>
        <button>X</button>
      </div>
    </li>
  );
};
